import { Component, OnInit } from '@angular/core';
import { PokeAPIServiceService } from '../poke-apiservice.service';
import { PokeShareInfoService } from '../poke-share-info.service';
import { PokeInfo, Pokemon } from '../pokemon';

@Component({
  selector: 'app-my-component',
  templateUrl: './my-component.component.html',
  styleUrls: ['./my-component.component.css'],
  providers: [PokeAPIServiceService]
})

export class MyComponentComponent implements OnInit{
  id: string = '';
  selectedPokeId: string = '';
  filterPokemon = '';
  pokemons: Pokemon[] = [];
  pokeInfo: PokeInfo | undefined ;
  cheminImage: any = "https://i.pinimg.com/564x/51/76/cf/5176cfe3e7967f5973e49971b409789c.jpg";

  constructor(private pokeService: PokeAPIServiceService,
    private pokeShareInfoService: PokeShareInfoService){
  }

  ngOnInit(): void {
    this.pokeService.getPokemons().subscribe((data) => {
      data.results.forEach((e, index) => {
        this.pokemons.push(new Pokemon('' + index, e.name, e.url));
      })
    });
  }

  go() {
    if(this.selectedPokeId != ''){
      this.pokeService.getPokemonInfo(this.selectedPokeId).subscribe(data => {
        this.pokeInfo = data;
        this.pokeShareInfoService.setValue(this.selectedPokeId);
      });
    }
  }

}
<div class="side-image-left"> </div>
<div class="side-image-right"> </div>

<div class="center">
    <input [(ngModel)]="id">

    <BR>
    Voila ce que j'écris plus haut : {{id}}

    <BR>
    Je te choisis : {{selectedPokeId}}

    <BR>
    <input [(ngModel)]="filterPokemon">

    <BR>
    <select [(ngModel)]="selectedPokeId">
        <option [value]="pokemon.id" *ngFor="let pokemon of pokemons | filterPokemonPipe:'name':filterPokemon"  >  {{pokemon.name}}  
        </option> 
    </select> 

    <BR>
    <button (click)="go()" >GO !</button>

    <BR>
    <div *ngIf="pokeInfo">
        <app-poke-info [info]="pokeInfo"> </app-poke-info>
    </div>
</div>
