import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { PokeInfo, Pokemon, PokeServiceRes } from './pokemon';

const url = 'https://pokeapi.co/api/v2/pokemon-species?limit=10&offset=0&language=${fr}';

@Injectable({
  providedIn: 'root'
})

export class PokeAPIServiceService {

  constructor(private http: HttpClient) {}

  getPokemons(language: string): Observable<PokeServiceRes> {
    const url = `${this.url}`;
    return this.http.get(url);
  }

  getPokemonInfo(id: String): Observable<PokeInfo> {
    return this.http.get<PokeInfo>(url + id + '/');
  }

}
