import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { PokeInfo } from './pokemon';

@Injectable({
  providedIn: 'root'
})
export class PokeAPIServiceService {
  private readonly baseUrl: string = 'https://pokeapi.co/api/v2';

  constructor(private http: HttpClient) {}

  getPokemons(language: string) {
    const url = `${this.baseUrl}/pokemon-species?limit=10&offset=0&language=${language}`;
    return this.http.get(url);
  }

  getPokemonInfo(id: string) {
    const url = `${this.baseUrl}/pokemon/${id}`;
    return this.http.get<PokeInfo>(url);
  }
}
