Le Pokémon séléctionné est {{info.name}} de types : 

<ul>
    <li *ngFor="let type of info.types"> {{type.type.name}} </li>
</ul>

<BR>

<img src={{info.sprites.front_default}}/>
<img src={{info.sprites.back_default}}/>
<img src={{info.sprites.front_shiny}}/>
<img src={{info.sprites.back_shiny}}/>

<BR>
Talents : 
<ul>
    <li *ngFor="let ability of info.abilities"> {{ability.ability.name}} </li>
</ul>


<BR>

Compétences : 
<ul>
    <li *ngFor="let move of info.moves"> {{move.move.name}} </li>
</ul>
import { Component, Input, OnInit } from '@angular/core';
import { PokeShareInfoService } from '../poke-share-info.service';
import { PokeInfo } from '../pokemon';

@Component({
  selector: 'app-poke-info',
  templateUrl: './poke-info.component.html',
  styleUrls: ['./poke-info.component.css'],
  providers: []
})

export class PokeInfoComponent implements OnInit{

  @Input('info')
  info!: PokeInfo;

  constructor(private pokeShareInfoService: PokeShareInfoService){
    this.pokeShareInfoService.getValue().subscribe(e => console.log(' e' + e))
  }

  ngOnInit(): void {}

}
